import React, { useState, useEffect } from "react";
import Neptune from "../assets/img/monstersAvatars/Neptune.png";
import monsters from "../data/monsters";

function Game() {
  const playerData = [
    {
      id: 1,
      life: 100,
      attack: 2,
      speed: 0,
      doodge: 0,
      accuracy: 1,
      armor: 0,
    },
  ];
  const [player, setplayer] = useState(playerData[0].attack);
  const [monsterLife, setMonsterLife] = useState(monsters[0].life);
  const [start, setStart] = useState(false);
  const [playerAttackAmount, setPlayerAttackAmount] = useState();
  const [monsteerDodgeChance, setMonsteerDodgeChance] = useState();

  const calculateMonsterDodgechance = () => {
    let playerAccuracity = playerData[0].accuracy;
    let monsterDodge = monsters[0].dodge;
    let checkDifference;
    if (playerAccuracity < monsterDodge) {
      checkDifference = Math.abs(playerAccuracity - monsterDodge);
    } else {
      checkDifference = 1;
    }
    let dodgeChance = Math.floor(Math.random() * checkDifference);
    setMonsteerDodgeChance(dodgeChance);
  };
  console.log(monsteerDodgeChance);
  useEffect(() => {
    if (start) {
      const interval = setInterval(() => {
        calculateMonsterDodgechance();
        if (monsteerDodgeChance === 0) {
          setMonsterLife((monsterLife) => monsterLife - player);
          setPlayerAttackAmount(playerdata[0].attack);
        }
      }, 1000);
      return () => clearInterval(interval);
    } else {
    }
  }, [start, monsteerDodgeChance]);
  return (
    <div className="game">
      <div className="fractalMonster">
        <div className="monsterAvatar">
          <img src={Neptune} alt="" />
          <div className="playerAttackAmount">
            {monsteerDodgeChance === 0
              ? playerAttackAmount
              : monsteerDodgeChance > 0
              ? "miss"
              : ""}
          </div>
        </div>
        <div
          className="monsterLifebar"
          style={{ width: monsterLife + "%" }}
        ></div>
        <p>{monsterLife}</p>
      </div>
      <div className={`startButton  ${start ? "displayNone" : ""}`}>
        <button onClick={() => setStart(true)}>start</button>
      </div>
      <div className="player">
        <div className="playerLifebar" style={{ width: 100 + "px" }}></div>
      </div>
    </div>
  );
}

export default Game;
