import React, { useState, useEffect } from "react";
import Neptune from "../assets/img/monstersAvatars/Neptune.png";

function Game() {
  const obj = [
    {
      ClientDatabase: "21",
      ClientID: "21",
      ClientName: "21",
      ClientServer: "21",
      Country: "US - 21",
      DatabaseVersion: "21",
      Namespace: "21",
    },
  ];
  const [player, setPlayer] = useState({
    life: 100,
    attack: 2,
  });
  const [monster, setMonster] = useState({
    life: 100,
    attack: 1,
  });
  console.log(monster.life);
  useEffect(() => {
    const interval = setInterval(() => {
      setMonster((monster) => monster.life - player.attack);
    }, 1000);
    return () => clearInterval(interval);
  }, []);
  return (
    <div className="game">
      <div className="fractalMonster">
        <div className="monsterAvatar">
          <img src={Neptune} alt="" />
        </div>
        <div
          className="monsterLifebar"
          style={{ width: monster.life + "px" }}
        ></div>
      </div>
      <div className="startButton">
        <button>start</button>
      </div>
      <div className="player">
        <div
          className="playerLifebar"
          style={{ width: monster.life + "px" }}
        ></div>
      </div>
    </div>
  );
}

export default Game;
