import React, { useState, useEffect } from "react";
import Neptune from "../assets/img/monstersAvatars/Neptune.png";
import monsters from "../data/monsters";

function Game() {
  const data = [
    {
      life: 100,
      attack: 2,
    },
    {
      life: 100,
      attack: 1,
    },
  ];
  const [player, setplayer] = useState(data[0].attack);
  const [monster, setMonster] = useState(data[1].life);
  const [start, setStart] = useState(false);
  const [monstersData, setMonstersData] = useState(monsters);
