    }
  }, [start]);
  return (
    <div className="game">
      <div className="fractalMonster">
        <div className="monsterAvatar">
          <img src={Neptune} alt="" />
        </div>
        <div className="monsterLifebar" style={{ width: monster + "px" }}></div>
        <p>{monster}</p>
      </div>
      <div className={`startButton  ${start ? "displayNone" : ""}`}>
        <button onClick={() => setStart(true)}>start</button>
      </div>
      <div className="player">
        <div
          className="playerLifebar"
          style={{ width: monster.life + "px" }}
        ></div>
      </div>
    </div>
  );
}

export default Game;
