import React, { useState, useEffect } from "react";
import { PlayerAttributesStorage } from "./PlayerAttributesStorage";

function PlayerSkills() {
  const [availableSkills, setAvailableSkills] = useState(3);
  const [playerAttack, setPlayerAttack] = PlayerAttributesStorage(
    "attack",
    "2"
  );
  console.log(localStorage);

  const handleAttack = () => {
    if (availableSkills < 0) {
      setPlayerAttack(parseInt(playerAttack) + 1);
      setAvailableSkills(availableSkills - 1);
    }
  };

  return (
    <div className="PlayerSkills">
      <div>
        <p>You gain a New Level!</p>
        <p>You have 3 skills avaliable </p>
        <div>
          <div>
            <span>Strength: </span>
            <span> {playerAttack}</span>
            <button onClick={() => handleAttack()}>+</button>
          </div>
          <div>
            <span>Vitality: </span>
            <span> 10</span>
            <button>+</button>
          </div>
          <div>
            <span>Agility: </span>
            <span> 10</span>
            <button>+</button>
          </div>
          <div>
            <span>Speed: </span>
            <span> 10</span>
            <button>+</button>
          </div>
          <div>
            <span>Accuracity: </span>
            <span> 10</span>
            <button>+</button>
          </div>
          <div>
            <span>Armor: </span>
            <span> 10</span>
            <button>+</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default PlayerSkills;
