import React, { useState, useEffect } from "react";
import Neptune from "../assets/img/monstersAvatars/Neptune.png";

function Game() {
  const data = [
    {
      life: 100,
      attack: 2,
    },
    {
      life: 100,
      attack: 1,
    },
  ];
  const [player, setplayer] = useState(data[0].attack);
  const [monster, setMonster] = useState(data[1].life);
  useEffect(() => {
    const interval = setInterval(() => {
      setMonster((monster) => monster - player);
    }, 1000);
    return () => clearInterval(interval);
  }, []);
  return (
    <div className="game">
      <div className="fractalMonster">
        <div className="monsterAvatar">
          <img src={Neptune} alt="" />
        </div>
        <div className="monsterLifebar" style={{ width: monster + "px" }}></div>
        <p>{monster}</p>
      </div>
      <div className="startButton">
        <button>start</button>
      </div>
      <div className="player">
        <div
          className="playerLifebar"
          style={{ width: monster.life + "px" }}
        ></div>
      </div>
    </div>
  );
}

export default Game;
