import React, { useState, useEffect } from "react";

function NextStage() {
  const [stageFloor, setStageFloor] = useState(
    localStorage.getItem("monsterLvl")
  );
  useEffect(() => {
    setStageFloor((stageFloor) => stageFloor + 1);
  });
  setStageFloor(stageFloor + 1);
  return (
    <div className="NextStage">
      <p>Stage {stageFloor}</p>
    </div>
  );
}

export default NextStage;
