import React from "react";
import { PlayerAttributesStorage } from "./PlayerAttributesStorage";

function PlayerSkills() {
  const [playerAttack, setPlayerAttack] = PlayerAttributesStorage(
    "attack",
    "2"
  );
  console.log(localStorage);

  const handleAttack = () => {
    setPlayerAttack("attack", "3");
  };

  return (
    <div className="PlayerSkills">
      <div>
        <p>You gain a New Level!</p>
        <p>You have 3 skills avaliable </p>
        <div>
          <div>
            <span>Strength: </span>
            <span> {playerAttack}</span>
            <button onClick={() => handleAttack()}>+</button>
          </div>
          <div>
            <span>Vitality: </span>
            <span> 10</span>
            <button>+</button>
          </div>
          <div>
            <span>Agility: </span>
            <span> 10</span>
            <button>+</button>
          </div>
          <div>
            <span>Speed: </span>
            <span> 10</span>
            <button>+</button>
          </div>
          <div>
            <span>Accuracity: </span>
            <span> 10</span>
            <button>+</button>
          </div>
          <div>
            <span>Armor: </span>
            <span> 10</span>
            <button>+</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default PlayerSkills;
